sys_variable_value.value:
(function execute(inputs, outputs) {
  outputs.propertystring = "";
  for (i = 0; i < inputs.properties.length; i++) {
    if (i > 0) {
      outputs.propertystring += ",";
    }
    outputs.propertystring += inputs.properties[i];
  }
  if (inputs.recursive == "true") {
    outputs.recursiveint = "1";
  } else {
    outputs.recursiveint = "0";
  }
})(inputs, outputs);

