sys_variable_value.value:
(function execute(inputs, outputs) {
  outputs.user_payload = {};
  outputs.user_payload.admin = inputs.is_admin;
  outputs.user_payload.email = inputs.email;
  outputs.user_payload.name = inputs.name;
  outputs.user_payload.password = inputs.password;
})(inputs, outputs);

