sys_variable_value.value:
(function execute(inputs, outputs) {
  var responseObj = JSON.parse(inputs.payload);
  var artifact = responseObj.artifacts[0];

  outputs.artifact_name = artifact.general.name;
  outputs.artifact_path = artifact.general.path;
  outputs.pkg_type = artifact.general.pkg_type;
  outputs.num_sec_issues = artifact.issues.length;
  outputs.num_licenses = artifact.licenses.length;

  outputs.top_severity = "None";
  var sev_index = {"None" : 0, "Low": 1, "Medium" : 2, "High" : 3, "Critial" : 4};
  for (var i = 0; i < artifact.issues.length; i++) {
    var severity = artifact.issues[i].severity; 
    if ( severity == "Critical") {
      outputs.top_severity = "Critical";
    } else if (sev_index[severity] > sev_index[outputs.top_severity]) {
      outputs.top_severity = severity;
    }
  }
})(inputs, outputs);

