sys_variable_value.value:
(function execute(inputs, outputs) {
  outputs.payload = {};

  outputs.payload.checksums = [];
  outputs.payload.checksums[0] = inputs.checksums;

  outputs.payload.paths = [];
  outputs.payload.paths[0] = inputs.paths;

})(inputs, outputs);

